mapper.select:
SELECT a.pid,a.tid
FROM
(
SELECT pid,tid FROM training_problem WHERE tid in

    #{tid}

) a
left join
(
SELECT DISTINCT pid FROM user_acproblem where uid = #{uid}
) b
on a.pid = b.pid
where b.pid is not null

select p.id
from training_problem tp,
     problem p
where tp.tid = #{tid}
  and tp.pid = p.id
  and p.auth = 1

select t.*
from tag t,
     problem_tag pt
where t.id = pt.tid
  and pt.pid = #{pid}
